var passport = require('passport');
var LocalStrategy = require('passport-local').Strategy;
var cookieSession = require('cookie-session')

passport.use(new LocalStrategy(
    function(username, password, done) {
        console.log('authenticate ');
        if (username === 'rakesh' && password === 'rawat') {
            done(null, {id: 'xxx'});
        } else {
            done('error', null);
        }
    }
));

passport.serializeUser(function(user, done) {
    done(null, user.id);
});

passport.deserializeUser(function(id, done) {
    done(err, {id: 'xxx'});
});

// Helper method to wait for a middleware to execute before continuing
// And to throw an error when an error happens in a middleware
function runMiddleware(req, res, fn) {
    return new Promise((resolve, reject) => {
      fn(req, res, result => {
        if (result instanceof Error) {
          return reject(result)
        }

        return resolve(result)
      })
    })
  }

async function handler(req, res) {
    console.log('API CALLED');

    await runMiddleware(req, res, cookieSession({
        session: 'sid',
        keys: ['aaaa']
    }))
    await runMiddleware(req, res, passport.initialize())
    await runMiddleware(req, res, passport.session())

    console.log('MIDDLEWARE RAN', req.session);

    passport.authenticate(
        'local',
        (err, result) => {
            console.log('AUTHENTICATION RAN', result);
            req.session = result;
            // Rest of the API logic
            res.json({ message: 'Hello Everyone!' })
        }
    )(req, res);
}

export default handler
